import React from "react";
import { useState } from "react";

function SignUpUser({ setU }) {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [passwordConfirmation, setPasswordConfirmation] = useState("");

  function handleSubmit(e) {
    e.preventDefault();
    fetch("https://pure-anchorage-05380.herokuapp.com/signup", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        username,
        email,
        password,
        password_confirmation: passwordConfirmation
      })
    })
    .then((r) => {
        if (r.ok) {
          r.json().then((user) => setUser(user));
        }
      });
    }

  return (
    <div>
      <p class="card-text">
        <form onSubmit={handleSubmit}>
          {/* <h4 class="card-title">{heading}</h4> */}
          <h1>Sign Up</h1>
          <fieldset>
            <div class="form-group">
              <label htmlFor="userEmail" class="form-label mt-4">
                Email-address:
              </label>
              <input
                type="email"
                class="form-control"
                id="userEmail"
                placeholder="Email"
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>

            <div class="form-group">
              <label htmlFor="userName" class="form-label mt-4">
                Username:
              </label>
              <input
                type="text"
                class="form-control"
                id="userName"
                placeholder="username"
                onChange={(e) => setUsername(e.target.value)}
              />
            </div>
            <div class="form-group">
              <label htmlFor="userPassword" class="form-label mt-4">
                Password:
              </label>
              <input
                type="password"
                class="form-control"
                id="userPassword"
                placeholder="Password"
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <div class="form-group">
              <label htmlFor="userConfirmPassword" class="form-label mt-4">
                Confirm-Password:
              </label>
              <input
                type="password"
                class="form-control"
                id="userPassword"
                placeholder="re-enter Password"
                onChange={(e) => setPasswordConfirmation(e.target.value)}
              />
            </div>
          </fieldset>
        </form>
      </p>
    </div>
  );
}

export default SignUpUser;
