import React from "react";
import { useState } from "react";

function SignUpUser({ setUser }) {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [passwordConfirm, setPasswordConfirm] = useState("");

  function handleSubmit() {
    fetch("https://pure-anchorage-05380.herokuapp.com/signUp", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        username,
        email,
        password,
        password_confirmation: passwordConfirm
      })
    }).then((r) => {
      if (r.ok) {
        r.json().then((user) => setUser(user));
        alert("user saved successfully");
      }
    });
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        {/* <h4 class="card-title">{heading}</h4> */}
        <h1>Sign Up</h1>
        <fieldset>
          <div class="form-group">
            <label htmlFor="userEmail" class="form-label mt-4">
              Emailaddress:
            </label>
            <input
              type="email"
              class="form-control"
              id="userEmail"
              value={email}
              placeholder="Email"
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>

          <div class="form-group">
            <label htmlFor="userName" class="form-label mt-4">
              Username:
            </label>
            <input
              type="text"
              class="form-control"
              id="userName"
              value={username}
              placeholder="username"
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div class="form-group">
            <label htmlFor="userPassword" class="form-label mt-4">
              Password:
            </label>
            <input
              class="form-control"
              type="password"
              id="password"
              value={password}
              placeholder="Password"
              onChange={(e) => setPassword(e.target.value)}
              autoComplete="current-password"
            />
          </div>
          <div class="form-group">
            <label htmlFor="userConfirmPassword" class="form-label mt-4">
              Confirm-Password:
            </label>
            <input
              type="password"
              class="form-control"
              id="userPassword"
              value={passwordConfirm}
              placeholder="re-enter Password"
              onChange={(e) => setPasswordConfirm(e.target.value)}
            />
          </div>
          <div class="d-grid gap-2 mt-3 w-25">
            <button class="btn btn-lg btn-primary text-nowrap" type="submit">
              Sign Up
            </button>
          </div>
        </fieldset>
      </form>
    </div>
  );
}

export default SignUpUser;
