import React, {useState} from "react";

function GalleryCard({ imgName, img, category, updatedAt }) {
  const [showReviewTextarea, setShowReviewTextarea] = useState(false)

  const ReviewTextBox = () => {
    setShowReviewTextarea(true);
  };
  return (
    <div>
      <div class="card m-4" style={{ maxWidth: "20rem" }}>
        <h3 class="card-header">{imgName}</h3>
        <svg
          xmlns="http://www.w3.org/2000/svg"
          class="d-block user-select-none"
          width="100%"
          height="200"
          aria-label="Placeholder: Image cap"
          focusable="false"
          role="img"
          preserveAspectRatio="xMidYMid slice"
          viewBox="0 0 318 180"
          style={{ fontSize: "1.125rem", textAnchor: "middle" }}
        >
          <rect width="100%" height="100%" fill="#868e96"></rect>
          <text x="50%" y="50%" fill="#dee2e6" dy=".3em">
            Image cap
          </text>
        </svg>

        <ul class="list-group list-group-flush">
          <li class="list-group-item">{category}</li>
        </ul>

        <div class="card-footer text-muted">{updatedAt}</div>
        {showReviewTextarea ===false ? (
          <button
          type="button"
          class="btn btn-primary btn-sm mt-2"
          onClick={ReviewTextBox}
        >
          Review
        </button>
        ) : (
          <div class="form-group">
            <label for="ReviewTextarea" class="form-label mt-4">
              Type your Review here...
            </label>
            <textarea class="form-control" id="ReviewTextarea" rows="2"></textarea>
            <button
          type="button"
          class="btn btn-primary btn-sm mt-2"
          onClick={ReviewTextBox}
        >
          Review
        </button>
          </div>

        )}

      </div>
    </div>
  );
}

export default GalleryCard;
