import React, { useState,useEffect } from "react";
import Navbar from "../Components/navbarComponent";
import GalleryCard from "../Components/galleryComponent";
// gallery page
function Gallery() {

  // declare initial states
  const [photos, setPhotos] = useState([]);
  const [photo, setPhoto] = useState({});

  const onOpenModal = (imageId) =>{
        console.lpog("MODAL DATA ", i)
  }

  // redirect to photo page for review
  const ReviewPage = () => {
    // navigate(`https://pure-anchorage-05380.herokuapp.com/${imgId}`);
    fetch(`https://pure-anchorage-05380.herokuapp.com/galleries/2`, {
      method: "GET",
      headers: { "Content-Type": "application/json" }
    })
      .then((res) => {
        res.json().then((data) => {
          console.log(data);
          setPhoto(data)
        });
      })
      .catch((err) => {
        alert(err);
      });
  };

  // get all images
  useEffect(() => {
      fetch("https://pure-anchorage-05380.herokuapp.com/galleries",{
      method: "GET",
      headers: { "Content-Type": "application/json" },
    })
      .then((res) => {
        res.json().then((data) => {
          setPhotos(data)
          console.log(data)
        });
      })
      .catch((err) => {
        console.log(err);
      });
    }, []);




  return (
    <div>
      <Navbar />

      {/* iterate over each and show in grid system */}
      <div className="row">
        {photos.map(({ id, title, image, category, date }) => {
          return (
            <div key={id} className="col-md-4">
              <GalleryCard
                key={id}
                imgId={id}
                imgName={title}
                imgUrl={image}
                category={category}
                updatedAt={date}
              />
            </div>
          );
        })}

      <button type="button" onClick={ReviewPage} class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#staticBackdrop">
        Review
      </button>

      <div class="modal fade" id="staticBackdrop" data-bs-backdrop="static" data-bs-keyboard="false" tabindex="-1" aria-labelledby="staticBackdropLabel" aria-hidden="true">
        <div class="modal-dialog modal-dialog-scrollable">
          <div class="modal-content">
            <div class="modal-header">
              <h1 class="modal-title fs-5" id="staticBackdropLabel">Modal title</h1>
              <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div class="modal-body">
              {photo.id}
            </div>
            <div class="modal-footer">
              <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
              <button type="button" class="btn btn-primary">Understood</button>
            </div>
          </div>
        </div>
      </div> 



      </div>
    </div>
  );
}

export default Gallery;
