import React, { useState } from "react";
// import { Button, Error, Input, FormField, Label } from "../styles";

function LoginForm({ onLogin }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errors, setErrors] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  function handleSubmit(e) {
    e.preventDefault();
    setIsLoading(true);
    fetch("/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({ username, password })
    }).then((r) => {
      setIsLoading(false);
      if (r.ok) {
        r.json().then((user) => onLogin(user));
      } else {
        r.json().then((err) => setErrors(err.errors));
      }
    });
  }
  //login form for user
  // function LoginForm() {
  const userRole = "User Login";
  return (
    <div>
      {/* <h4 class="card-title">{userRole}</h4>
      <p class="card-text"> */}
        <form onSubmit={handleSubmit}>
          <fieldset>
            <div class="form-group">
              <label htmlFor="userEmail" class="form-label mt-4">
                Username
              </label>
              <input
                type="email"
                class="form-control"
                id="userEmail"
                placeholder="Email"

                type="text"
          id="username"
          autoComplete="off"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
              />
            </div>
            <div class="form-group">
              <label for="userPassword" class="form-label mt-4">
                Password
              </label>
              <input
                type="password"
                class="form-control"
                id="userPassword"
                placeholder="Password"
              />
            </div>
            {/* sign-in and sign-up */}
            <div class="d-grid gap-2 mt-3 w-25">
              <button class="btn btn-lg btn-primary" type="button">
                Login
              </button>
            </div>
            <div class="d-grid gap-2 mt-3">
              <button class="btn btn-lg btn-warning mt-2" type="button">
                Don't Have an account? Create Account
              </button>
            </div>
          </fieldset>
        </form>
    </div>
  );
}

export default LoginForm;
