import React, { useState } from "react";

function Review() {

  // declare initial states
  const [photo, setPhoto] = useState({});

  // get single images
  const getOneImages = () => {
    fetch(`http://127.0.0.1:3000/galleries/${imageId}`, {
      method: "GET",
      headers: { "Content-Type": "application/json" },
    })
      .then((res) => {
        res.json().then((data) => setPhoto(data));
      })
      .catch((err) => {
        console.log(err);
      });
  };

  // call function
  getOneImages();

  // post review
  const submitReview = () => {};
  return (
    <div className="container w-50">
      <div class="card m-4" style={{ maxWidth: "40rem" }}>
        <h3 class="card-header">{photo.title}</h3>
        <img
          src={photo.image}
          class="d-block user-select-none"
          alt={photo.title}
          width="100%"
          height="100%"
        />

        <ul class="list-group list-group-flush">
          <li class="list-group-item">{photo.category}</li>
        </ul>

        <div class="card-footer text-muted">{photo.date}</div>
      </div>
      <div class="form-group">
        <label for="ReviewTextarea" class="form-label mt-4">
          Type your Review here...
        </label>
        <textarea class="form-control" id="ReviewTextarea" rows="2"></textarea>
        <div class="d-flex justify-content-center mb-2">
          <button
            type="button"
            class="btn btn-primary btn-sm mt-2"
            onClick={submitReview}
          >
            Submit
          </button>
        </div>
      </div>
    </div>
  );
}

export default Review;
