import React from "react";

function Review() {
  let { id, img, imgName, category, updatedAt } = {
    id: "1",
    imgName: "Irene",
    img: "https://media.istockphoto.com/photos/global-connection-picture-id1335295270?b=1&k=20&m=1335295270&s=170667a&w=0&h=PNF6QH5FyD_XvDbn4nHtIVKdmmlN86fCHTgwvkZYvHA=",
    category: "",
    updatedAt: "2 days ago",
  };

  // post review
  const submitReview = () => {};
  return (
    <div className="container w-50">
      <div class="card m-4" style={{ maxWidth: "40rem", maxHeight: "uto" }}>
        <h3 class="card-header">{imgName}</h3>
        <img
          src={img}
          class="d-block user-select-none"
          alt={imgName}
          width="100%"
          height="200"
        />

        <ul class="list-group list-group-flush">
          <li class="list-group-item">{category}</li>
        </ul>

        <div class="card-footer text-muted">{updatedAt}</div>
        {/* <button
          type="button"
          class="btn btn-primary btn-sm mt-2"
          onClick={ReviewPage}
        >
          Review
        </button> */}
      </div>
      <div class="form-group">
        <label for="ReviewTextarea" class="form-label mt-4">
          Type your Review here...
        </label>
        <textarea class="form-control" id="ReviewTextarea" rows="2"></textarea>
        <div class="d-flex justify-content-center mb-2">
          <button
            type="button"
            class="btn btn-primary btn-sm mt-2"
            onClick={submitReview}
          >
            Submit
          </button>
        </div>
      </div>
    </div>
  );
}

export default Review;
