import React from "react";

function GalleryCard({ imgId, imgName, imgUrl, category, updatedAt }) {
  // declare navigate
  // const navigate = useNavigate();
  // redirect to photo page for review
  const ReviewPage = () => {
    // navigate(`https://pure-anchorage-05380.herokuapp.com/${imgId}`);
    fetch(`https://pure-anchorage-05380.herokuapp.com/galleries/${imgId}`, {
      method: "GET",
      headers: { "Content-Type": "application/json" }
    })
      .then((res) => {
        res.json().then((data) => {
          console.log(data);
          setPhoto(data)
        });
      })
      .catch((err) => {
        alert(err);
      });
  };
  return (
    <div>
      <div className="card m-4" style={{ maxWidth: "20rem" }}>
        <h3 className="card-header">{imgName}</h3>
        <img
          src={imgUrl}
          className="d-block user-select-none"
          alt={imgName}
          width="100%"
          height="200"
        />

        <ul className="list-group list-group-flush">
          <li className="list-group-item">{category}</li>
        </ul>

        <div className="card-footer text-muted">{updatedAt}</div>
        {/* <button
          type="button"
          className="btn btn-primary btn-sm mt-2"
          onClick={ReviewPage}
        >
          Review
        </button> */}   

      <button type="button" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#staticBackdrop">
        Review
      </button>

      <div class="modal fade" id="staticBackdrop" data-bs-backdrop="static" data-bs-keyboard="false" tabindex="-1" aria-labelledby="staticBackdropLabel" aria-hidden="true">
        <div class="modal-dialog modal-dialog-scrollable">
          <div class="modal-content">
            <div class="modal-header">
              <h1 class="modal-title fs-5" id="staticBackdropLabel">Modal title</h1>
              <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div class="modal-body">
              ...
            </div>
            <div class="modal-footer">
              <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
              <button type="button" class="btn btn-primary">Understood</button>
            </div>
          </div>
        </div>
      </div>






      </div>
    </div>
  );
}

export default GalleryCard;
