import React from "react";
import { useNavigate } from "react-router-dom";

function GalleryCard({ id, imgName, img, category, updatedAt }) {
  // declare navigate
  const navigate = useNavigate();
  // redirect to photo page for review
  const ReviewPage = () => {
    navigate(`/gallery/${id}`);
  };
  return (
    <div>
      <div className="card m-4" style={{ maxWidth: "20rem" }}>
        <h3 className="card-header">{imgName}</h3>
        <img
          src={img}
          class="d-block user-select-none"
          alt={imgName}
          width="100%"
          height="200"
        />

        <ul class="list-group list-group-flush">
          <li class="list-group-item">{category}</li>
        </ul>

        <div class="card-footer text-muted">{updatedAt}</div>
        <button
          type="button"
          class="btn btn-primary btn-sm mt-2"
          onClick={ReviewPage}
        >
          Review
        </button>
      </div>
    </div>
  );
}

export default GalleryCard;
