// imports
import React from "react";
import BookingForm from "./bookingForm";

// booking button
function BookUs({showForm, visibleForm}) {
  return (
    <div class="d-flex justify-content-center mt-3" >
      {visibleForm === true ? (
        <BookingForm />
      ) : (
      <div class="d-grid gap-2">
        <button class="btn btn-outline-success text-nowrap" type="button" onClick={showForm()}>
          Book Us
        </button>
      </div>
      )}
    </div>
  );
}

export default BookUs;
