// import Carousel from "react-bootstrap/Carousel";
import React from "react";
import { Carousel } from "react-carousel-minimal";

function CarouseLComponent() {
  const data = [
    {
      image:
     "https://i.pinimg.com/564x/19/9c/df/199cdf8d8948b2839ceb99a1d891b867.jpg",
      // caption: "LOOKING FOR THE <br> RIGHT VEHICLE REPAIR ?",
    },
    {
      image:
      "https://i.pinimg.com/236x/7b/3a/28/7b3a283319c2d4d0a6cf4fa2990f3338.jpg",
      // caption: "QUALITY CAR SERVICE",
    },
    {
      image:
      "https://i.pinimg.com/564x/f7/a9/ef/f7a9efa5ac3b60f9d5c8ec764e83f3df.jpg",
      // caption: "YOUR ONE STOP <br> CAR CARE SERVICES",
    },
    {
      // image:
      //  ,
      // caption: "EFFICIENT TECHNICIANS <br> WILL SOLVE ANY PROBLEM",
    },
    {
      // image:
      //   ,
      // caption: "LET US MIND MORE ABOUT YOUR CAR",
    },
  ];


  const captionStyle = {
    fontSize: "3em",
    fontWeight: "bold",
    width: "70%",
    color: "pinkie",
    marginBottom: "10px",
    marginTop: "10px",
  };

  return (
    <div className="mt-25">
      <div style={{ textAlign: "center" }}>
        <div style={{}}>
          <Carousel
            data={data}
            time={2000}
            width="auto"
            height="50px"
            captionStyle={captionStyle}
            captionPosition="center"
            automatic={true}
            dots={true}
            pauseIconColor="white"
            pauseIconSize="40px"
            slideImageFit="fit"
            style={{
              textAlign: "center",
              maxWidth: "auto",
            }}
          />
        </div>
      </div>
    </div>
  );

}
export default CarouseLComponent;
